{#each news as n}
	<a href="{n.url}">
		<div class="news-container">
			<h4 class="news-title">{n.title}</h4>
			<p class="news-content">{n.content}</p>
		</div>
	</a>
{/each}

<style>
	.news-container	{
		margin-left: 5em;
		margin-right: 5em;
		margin-top: .8em;
		margin-bottom: .8em;

		padding: .5em;
		
		border: 2px solid #016bb7;
		border-radius: 24px;
	}
	.news-content	{
		text-overflow: ellipsis;
		overflow: hidden;
		white-space: nowrap;

		width: 100%;
		height: 20px;
	}
	a	{
		text-decoration: none;
	}
	a:visited	{
		color: #000;
	}
	.news-title	{
		color: #272f5f
	}
</style>